    public static ArrayList<Nama_Rasul> getDataRasul(){
        ArrayList<Nama_Rasul> rasuls = new ArrayList<>();
        for (int i = 0; i < namaRasul.length; i++){
            Nama_Rasul nama_rasul = new Nama_Rasul();
            nama_rasul.setNamaRasul(namaRasul[i]);
            nama_rasul.setPerayaan(perayaan[i]);
            nama_rasul.setLahir(lahir[i]);
            nama_rasul.setKota_asal(kota_asal[i]);
            nama_rasul.setWilayah_karya(wilayah_karya[i]);
            nama_rasul.setWafat(wafat[i]);
            nama_rasul.setBeatifikasi(beatifikasi[i]);
            nama_rasul.setPhotoRasul(photo[i]);
            nama_rasul.setDetail(detailRasul[i]);
            nama_rasul.setKanoniasi(kanoniasi[i]);
            nama_rasul.setSumber(sumber[i]);
            rasuls.add(nama_rasul);
        }
        return rasuls;
    }

